ALNApplication.usage_example:
NSError *error = nil;
ALNApplication *app = [[ALNApplication alloc] initWithEnvironment:@"development"
                                                     configRoot:@"config"
                                                          error:&error];
if (app == nil) {
  NSLog(@"startup config failed: %@", error);
  return;
}

[app registerRouteMethod:@"GET"
                    path:@"/healthz"
                    name:@"healthz"
         controllerClass:[HealthController class]
                  action:@"show"];

if (![app startWithError:&error]) {
  NSLog(@"app startup failed: %@", error);
}

ALNHTTPServer.usage_example:
ALNHTTPServer *server = [[ALNHTTPServer alloc] initWithApplication:app
                                                          publicRoot:@"public"];
int exitCode = [server runWithHost:@"127.0.0.1"
                      portOverride:3000
                              once:NO];

ALNContext.usage_example:
NSString *userID = [context stringParamForName:@"id"];
if (userID == nil) {
  [context addValidationErrorForField:@"id"
                                 code:@"required"
                              message:@"id is required"];
}

ALNController.usage_example:
- (void)showUser {
  NSString *userID = [self stringParamForName:@"id"];
  if (userID == nil) {
    [self addValidationErrorForField:@"id"
                                code:@"required"
                             message:@"id is required"];
    [self renderValidationErrors];
    return;
  }

  NSDictionary *payload = @{ @"id": userID, @"status": @"ok" };
  NSError *error = nil;
  [self renderJSONEnvelopeWithData:payload meta:nil error:&error];
}

ALNPg.usage_example:
NSError *error = nil;
ALNPg *db = [[ALNPg alloc] initWithConnectionString:@"postgres://localhost/arlen"
                                     maxConnections:4
                                              error:&error];
NSArray *rows = [db executeQuery:@"SELECT now() AS ts"
                      parameters:@[]
                           error:&error];

ALNSQLBuilder.usage_example:
NSError *error = nil;
ALNSQLBuilder *builder = [[[ALNSQLBuilder selectFrom:@"users" columns:@[@"id", @"email"]]
    whereField:@"status" equals:@"active"]
    orderByField:@"id" descending:NO];
NSString *sql = [builder buildSQL:&error];
NSArray *params = [builder buildParameters:&error];

ALNRealtimeHub.usage_example:
ALNRealtimeHub *hub = [ALNRealtimeHub sharedHub];
NSUInteger delivered = [hub publishMessage:@"status:ok" onChannel:@"system"];
NSLog(@"delivered to %lu subscribers", (unsigned long)delivered);
